<template>
  <div class="col-sm-6 col-md-4 col-lg-3 py-3">
    <a @click="routeToProjectDetail" v-bind:href="get_route">
      <div class="car rounded-3 shadow h-100 transition-2 pjboard">
        <img class="card-image" src="data:image/jpeg;base64,">
     
        <div class="card-body py-3">
          <h4>{{ project.name }}</h4>
          <p>{{ project.description }}</p>
          <p>Manager: {{ project.manager.name }}</p>
        </div>
      </div>
    </a>
  </div>
</template>

<script>
export default {
  props: {
    project: null,
  },
  methods: {
    routeToProjectDetail() {
      this.$router.push(this.get_route());
    },
  },
  computed: {
    get_route() {
      return `/project/${this.project.id}`;
    },
  },
};
</script>

